import { describe, it, expect, beforeEach } from 'vitest';
import { AuditLogger, AuditLogEntry } from '../../src/core/mcp/AuditLogger';
import { validMCP } from '../../__mocks__/contexts/validMCP';
import { emptyMCP } from '../../__mocks__/contexts/emptyMCP';
import { partialMCP } from '../../__mocks__/contexts/partialMCP';

/**
 * EVALUACI√ìN DEL AUDITLOGGER
 * 
 * Esta suite eval√∫a el comportamiento del AuditLogger en diversos escenarios:
 * 1. Registrar actualizaciones de bloques v√°lidos ‚Üí debe crear entradas de log correctas
 * 2. Registrar actualizaciones con datos vac√≠os ‚Üí debe manejar el caso sin errores
 * 3. Registrar actualizaciones con datos parciales ‚Üí debe filtrar y registrar solo cambios reales
 * 4. Gesti√≥n de la persistencia de logs ‚Üí debe almacenar y permitir recuperar los logs
 */
describe('AuditLogger EVAL', () => {
  // Limpiar los logs antes de cada prueba
  beforeEach(() => {
    AuditLogger.clearAuditLogs();
  });

  /**
   * CASO 1: Registrar actualizaciones de bloques v√°lidos
   * 
   * El logger debe crear entradas de log correctas cuando hay cambios
   * entre bloques originales y actualizados
   */
  describe('Caso 1: Registro de actualizaciones v√°lidas', () => {
    it('debe registrar correctamente las actualizaciones de bloques', () => {
      // Preparar datos de prueba
      const originalBlocks = validMCP.contextual.data.map(block => ({ ...block }));
      const updatedBlocks = originalBlocks.map(block => {
        if (block.id === 'ctx-1') {
          return { ...block, content: block.content + ' [ACTUALIZADO]' };
        }
        return { ...block };
      });

      // Registrar la actualizaci√≥n
      AuditLogger.logBlockUpdates(
        originalBlocks, 
        updatedBlocks, 
        'test-user-001', 
        'visit-20230501-01'
      );

      // Verificar los logs
      const logs = AuditLogger.getAuditLogs();

      // Comprobar que se ha registrado solo un cambio
      expect(logs.length).toBe(1);

      // Verificar la estructura y contenido del log
      const logEntry = logs[0];
      expect(logEntry.block_id).toBe('ctx-1');
      expect(logEntry.operation).toBe('update');
      expect(logEntry.user_id).toBe('test-user-001');
      expect(logEntry.visit_id).toBe('visit-20230501-01');
      expect(logEntry.old_content).toBe(originalBlocks.find(b => b.id === 'ctx-1')?.content);
      expect(logEntry.new_content).toBe(updatedBlocks.find(b => b.id === 'ctx-1')?.content);
      expect(logEntry.block_type).toBe('contextual');
      expect(logEntry.timestamp).toBeDefined();
    });
  });

  /**
   * CASO 2: Registrar actualizaciones con datos vac√≠os
   * 
   * El logger debe manejar correctamente el caso de datos vac√≠os,
   * evitando errores y no creando logs innecesarios
   */
  describe('Caso 2: Registro con datos vac√≠os', () => {
    it('debe manejar correctamente arrays de bloques vac√≠os', () => {
      // Usar arrays vac√≠os
      AuditLogger.logBlockUpdates([], [], 'test-user-001', 'visit-empty');

      // Verificar que no se crearon logs
      const logs = AuditLogger.getAuditLogs();
      expect(logs.length).toBe(0);
    });

    it('debe manejar correctamente el caso donde solo hay bloques originales', () => {
      // Solo hay bloques originales, sin actualizaciones
      AuditLogger.logBlockUpdates(
        emptyMCP.contextual.data, 
        [], 
        'test-user-001', 
        'visit-empty'
      );

      // Verificar que no se crearon logs
      const logs = AuditLogger.getAuditLogs();
      expect(logs.length).toBe(0);
    });
  });

  /**
   * CASO 3: Registrar actualizaciones con datos parciales
   * 
   * El logger debe filtrar y registrar solo los cambios reales,
   * manejando correctamente datos parciales o incompletos
   */
  describe('Caso 3: Registro con datos parciales', () => {
    it('debe registrar solo los bloques que realmente cambiaron', () => {
      // Preparar bloques parciales
      const originalBlocks = partialMCP.contextual.data.map(block => ({ ...block }));
      // Crear una copia con m√∫ltiples cambios, pero solo uno real
      const updatedBlocks = originalBlocks.map(block => ({ ...block }));

      // Modificar solo un bloque
      if (updatedBlocks.length > 0) {
        updatedBlocks[0].content = 'Contenido modificado para prueba';
      }

      // Registrar la actualizaci√≥n
      AuditLogger.logBlockUpdates(
        originalBlocks, 
        updatedBlocks, 
        'test-user-001', 
        'visit-partial'
      );

      // Verificar los logs
      const logs = AuditLogger.getAuditLogs();

      // Deber√≠a haber un solo log (solo cambi√≥ un bloque)
      expect(logs.length).toBe(1);

      // Verificar que el log corresponde al bloque que cambi√≥
      expect(logs[0].block_id).toBe(originalBlocks[0].id);
      expect(logs[0].old_content).toBe(originalBlocks[0].content);
      expect(logs[0].new_content).toBe('Contenido modificado para prueba');
    });

    it('debe ignorar bloques sin ID', () => {
      // Preparar bloques con y sin ID
      const originalBlocks = [
        { id: 'valid-1', content: 'Original 1', type: 'contextual' },
        { content: 'Sin ID', type: 'contextual' } // Intencionalmente sin ID
      ];

      const updatedBlocks = [
        { id: 'valid-1', content: 'Updated 1', type: 'contextual' },
        { content: 'Sin ID actualizado', type: 'contextual' } // Sigue sin ID
      ];

      // Registrar la actualizaci√≥n
      AuditLogger.logBlockUpdates(
        originalBlocks, 
        updatedBlocks, 
        'test-user-001', 
        'visit-test'
      );

      // Verificar los logs
      const logs = AuditLogger.getAuditLogs();

      // Solo deber√≠a registrar el bloque con ID v√°lido
      expect(logs.length).toBe(1);
      expect(logs[0].block_id).toBe('valid-1');
    });
  });

  /**
   * CASO 4: Gesti√≥n de la persistencia de logs
   * 
   * El logger debe almacenar y permitir recuperar los logs correctamente,
   * manteniendo su integridad y estructura
   */
  describe('Caso 4: Persistencia de logs', () => {
    it('debe acumular m√∫ltiples logs en sesiones sucesivas', () => {
      // Primera sesi√≥n de logging
      AuditLogger.logBlockUpdates(
        [{ id: 'block-1', content: 'Original 1', type: 'contextual' }],
        [{ id: 'block-1', content: 'Updated 1', type: 'contextual' }],
        'user-1',
        'visit-1'
      );

      // Segunda sesi√≥n de logging
      AuditLogger.logBlockUpdates(
        [{ id: 'block-2', content: 'Original 2', type: 'persistent' }],
        [{ id: 'block-2', content: 'Updated 2', type: 'persistent' }],
        'user-2',
        'visit-2'
      );

      // Verificar que ambos logs est√°n almacenados
      const logs = AuditLogger.getAuditLogs();
      expect(logs.length).toBe(2);

      // Verificar que los logs mantienen sus datos correctos
      const log1 = logs.find(log => log.block_id === 'block-1');
      const log2 = logs.find(log => log.block_id === 'block-2');

      expect(log1).toBeDefined();
      expect(log2).toBeDefined();
      expect(log1?.user_id).toBe('user-1');
      expect(log2?.user_id).toBe('user-2');
    });

    it('debe proporcionar una copia de los logs, no una referencia', () => {
      // Registrar un log
      AuditLogger.logBlockUpdates(
        [{ id: 'block-1', content: 'Original', type: 'contextual' }],
        [{ id: 'block-1', content: 'Updated', type: 'contextual' }],
        'user-1',
        'visit-1'
      );

      // Obtener los logs
      const logs = AuditLogger.getAuditLogs();
      const initialCount = logs.length;

      // Intentar modificar el array devuelto
      logs.push({
        timestamp: new Date().toISOString(),
        user_id: 'hacker',
        visit_id: 'fake',
        block_id: 'fake',
        block_type: 'contextual',
        operation: 'update',
        old_content: 'hack',
        new_content: 'hack'
      });

      // Verificar que el array interno no fue modificado
      const freshLogs = AuditLogger.getAuditLogs();
      expect(freshLogs.length).toBe(initialCount);
    });
  });
});

import { describe, it, expect, vi } from 'vitest';
import { getAgentSuggestions, AgentSuggestion } from '../../src/core/agent/ClinicalAgent';
import { buildAgentContext } from '../../src/core/agent/AgentContextBuilder';
import { validMCP } from '../../__mocks__/contexts/validMCP';
import { emptyMCP } from '../../__mocks__/contexts/emptyMCP';
import { contradictoryMCP } from '../../__mocks__/contexts/contradictoryMCP';
import { chronicMCP } from '../../__mocks__/contexts/chronicMCP';
import { MCPContext } from '../../src/core/mcp/schema';

/**
 * Evaluaci√≥n del Agente Cl√≠nico
 * 
 * Esta suite de pruebas eval√∫a el comportamiento del agente cl√≠nico
 * frente a diferentes contextos cl√≠nicos, verificando que:
 * 
 * 1. Genere sugerencias coherentes y con sentido cl√≠nico en contextos v√°lidos
 * 2. Genere fallbacks seguros con contextos vac√≠os
 * 3. Detecte y maneje adecuadamente informaci√≥n contradictoria o inconsistente
 * 4. Proponga seguimiento o intervenci√≥n adecuada para casos cr√≥nicos
 */
describe('ClinicalAgent EVAL', () => {
  /**
   * Caso 1: Contexto cl√≠nico v√°lido
   * 
   * El agente debe generar sugerencias coherentes con el contexto m√©dico
   * incluyendo recomendaciones relacionadas con dolor, presi√≥n arterial y diabetes
   */
  describe('Caso 1: Contexto cl√≠nico v√°lido', () => {
    it('debe generar 2-3 sugerencias con explicaciones para un contexto v√°lido', async () => {
      // Construir el contexto del agente a partir del MCP v√°lido
      const agentContext = buildAgentContext(validMCP);

      // Obtener sugerencias del agente
      const suggestions = await getAgentSuggestions(agentContext);

      // Verificar que se generan al menos 2 sugerencias
      expect(suggestions.length).toBeGreaterThanOrEqual(2);
      expect(suggestions.length).toBeLessThanOrEqual(5); // No demasiadas para evitar sobrecarga de informaci√≥n

      // Verificar que todas las sugerencias tienen un ID y contenido
      suggestions.forEach(suggestion => {
        expect(suggestion.id).toBeDefined();
        expect(suggestion.content.length).toBeGreaterThan(10);
        expect(suggestion.type).toMatch(/recommendation|warning|info/);
      });

      // Verificar que al menos una sugerencia est√° relacionada con dolor tor√°cico (contexto clave)
      const hasDolor = suggestions.some(s => 
        s.content.toLowerCase().includes('dolor') || 
        s.content.toLowerCase().includes('tor√°cico')
      );

      // Verificar que al menos una sugerencia est√° relacionada con tensi√≥n arterial (contexto clave)
      const hasTension = suggestions.some(s => 
        s.content.toLowerCase().includes('tensi√≥n') || 
        s.content.toLowerCase().includes('arterial') || 
        s.content.toLowerCase().includes('presi√≥n')
      );

      // Verificamos que al menos una de las sugerencias relevantes existe
      expect(hasDolor || hasTension).toBe(true);
    });

    it('debe referenciar correctamente los bloques de origen de las sugerencias', async () => {
      const agentContext = buildAgentContext(validMCP);
      const suggestions = await getAgentSuggestions(agentContext);

      // Verificar que cada sugerencia tiene un sourceBlockId v√°lido
      suggestions.forEach(suggestion => {
        expect(suggestion.sourceBlockId).toBeDefined();

        // Comprobar que el ID referencia a un bloque existente
        const blockExists = agentContext.blocks.some(block => block.id === suggestion.sourceBlockId);
        expect(blockExists).toBe(true);
      });
    });
  });

  /**
   * Caso 2: Contexto vac√≠o
   * 
   * El agente deber√≠a generar sugerencias gen√©ricas/fallbacks con un contexto vac√≠o
   */
  describe('Caso 2: Contexto vac√≠o', () => {
    it('debe generar sugerencias gen√©ricas para un contexto vac√≠o', async () => {
      // Construir el contexto del agente a partir del MCP vac√≠o
      const agentContext = buildAgentContext(emptyMCP);

      // Obtener sugerencias del agente
      const suggestions = await getAgentSuggestions(agentContext);

      // La implementaci√≥n original genera dos sugerencias gen√©ricas
      expect(suggestions.length).toBe(2);

      // Verificamos que sean recomendaciones generales seguras
      suggestions.forEach(suggestion => {
        // Verificar que son sugerencias gen√©ricas
        expect(suggestion.type).toMatch(/info|recommendation/);

        // Las sugerencias no deben incluir t√©rminos m√©dicos espec√≠ficos 
        // que requieran conocimiento del caso particular
        const noSpecificDisease = !suggestion.content.match(/diabetes|hipertensi√≥n|c√°ncer|EPOC|arritmia|insuficiencia/i);
        expect(noSpecificDisease).toBe(true);

        // No debe incluir medicamentos espec√≠ficos sin contexto
        const noSpecificMeds = !suggestion.content.match(/metformina|insulina|enalapril|losart√°n|hidroclorotiazida|opioides/i);
        expect(noSpecificMeds).toBe(true);
      });
    });
  });

  /**
   * Caso 3: Contexto con datos contradictorios
   * 
   * Evaluamos c√≥mo el agente maneja informaci√≥n contradictoria.
   * 
   * Nota: La implementaci√≥n actual se basa en palabras clave y no tiene detecci√≥n
   * de contradicciones, pero verificamos que genere sugerencias relevantes para
   * las palabras clave presentes.
   */
  describe('Caso 3: Contexto con datos contradictorios', () => {
    it('debe generar sugerencias relevantes incluso con informaci√≥n contradictoria', async () => {
      // Construir el contexto del agente a partir del MCP contradictorio
      const agentContext = buildAgentContext(contradictoryMCP);

      // Obtener sugerencias del agente
      const suggestions = await getAgentSuggestions(agentContext);

      // Verificar que se generan sugerencias (al menos las gen√©ricas)
      expect(suggestions.length).toBeGreaterThan(0);

      // Verificar si el contexto incluye palabras clave que activar√≠an sugerencias espec√≠ficas
      const containsKeywords = agentContext.blocks.some(block => {
        const content = block.content.toLowerCase();
        return content.includes('dolor') || 
               content.includes('presi√≥n') || 
               content.includes('tension') || 
               content.includes('diabetes') || 
               content.includes('glucosa');
      });

      if (containsKeywords) {
        // Si hay palabras clave, debe haber al menos una sugerencia espec√≠fica
        const hasSpecificSuggestion = suggestions.some(s => 
          s.content.toLowerCase().includes('dolor') || 
          s.content.toLowerCase().includes('tensi√≥n') || 
          s.content.toLowerCase().includes('diabetes') || 
          s.content.toLowerCase().includes('glucemia')
        );

        expect(hasSpecificSuggestion).toBe(true);
      } else {
        // Si no hay palabras clave, debe haber al menos las sugerencias gen√©ricas
        expect(suggestions.length).toBeGreaterThanOrEqual(2);
      }
    });
  });

  /**
   * Caso 4: Contexto de enfermedad cr√≥nica
   * 
   * Evaluamos c√≥mo el agente maneja casos cr√≥nicos.
   * 
   * Nota: La implementaci√≥n actual no tiene detecci√≥n espec√≠fica para casos cr√≥nicos,
   * pero verificamos que genere sugerencias relevantes para el contexto dado.
   */
  describe('Caso 4: Contexto de enfermedad cr√≥nica', () => {
    it('debe generar sugerencias relevantes para el contexto de enfermedad cr√≥nica', async () => {
      // Construir el contexto del agente a partir del MCP de caso cr√≥nico
      const agentContext = buildAgentContext(chronicMCP);

      // Obtener sugerencias del agente
      const suggestions = await getAgentSuggestions(agentContext);

      // Verificar que se generan sugerencias
      expect(suggestions.length).toBeGreaterThan(0);

      // Verificar si el contexto incluye palabras clave que activar√≠an sugerencias espec√≠ficas
      const containsKeywords = agentContext.blocks.some(block => {
        const content = block.content.toLowerCase();
        return content.includes('dolor') || 
               content.includes('presi√≥n') || 
               content.includes('tension') || 
               content.includes('diabetes') || 
               content.includes('glucosa');
      });

      if (containsKeywords) {
        // Si hay palabras clave, debe haber al menos una sugerencia espec√≠fica
        const hasSpecificSuggestion = suggestions.some(s => 
          s.content.toLowerCase().includes('dolor') || 
          s.content.toLowerCase().includes('tensi√≥n') || 
          s.content.toLowerCase().includes('diabetes') || 
          s.content.toLowerCase().includes('glucemia')
        );

        expect(hasSpecificSuggestion).toBe(true);
      } else {
        // Si no hay palabras clave, debe haber al menos las sugerencias gen√©ricas
        expect(suggestions.length).toBeGreaterThanOrEqual(2);
      }

      // Verificar que todas las sugerencias tienen la estructura correcta
      suggestions.forEach(suggestion => {
        expect(suggestion.id).toBeDefined();
        expect(suggestion.sourceBlockId).toBeDefined();
        expect(suggestion.type).toMatch(/recommendation|warning|info/);
        expect(suggestion.content.length).toBeGreaterThan(10);
