export class AuditLogger {
  /**
   * Registra un evento en el sistema de auditor√≠a
   * @param eventType Tipo de evento a registrar
   * @param payload Datos asociados al evento
   */
  static log(
    action: string,
    metadata: {
      userId: string;
      visitId: string;
      patientId: string;
      [key: string]: unknown;
    }
  ): void {
    const entry: AuditLogEntry = {
      id: crypto.randomUUID(),
      user_id: metadata.userId,
      action,
      visit_id: metadata.visitId,
      patient_id: metadata.patientId,
      timestamp: new Date().toISOString(),
      metadata,
      event_type: action
    };

    console.log('Audit log entry:', entry);
  }

  /**
   * Registra la integraci√≥n de una sugerencia en el EMR
   * @param userId ID del usuario que realiza la acci√≥n
   * @param visitId ID de la visita
   * @param suggestionId ID de la sugerencia
   * @param suggestionType Tipo de sugerencia
   * @param content Contenido de la sugerencia
   * @param emrSection Secci√≥n del EMR donde se integr√≥
   */
  static logSuggestionIntegration(
    userId: string,
    visitId: string,
    suggestionId: string,
    suggestionType: 'recommendation' | 'warning' | 'info',
    content: string,
    section: string
  ): void {
    this.log('suggestion.integration', {
      userId,
      visitId,
      patientId: '', // Necesitamos obtener el patientId de alguna manera
      suggestionId,
      suggestionType,
      content,
      section
    });
  }

  /**
   * Registra la retroalimentaci√≥n proporcionada sobre una sugerencia cl√≠nica
   * @param userId ID del usuario que proporciona la retroalimentaci√≥n
   * @param visitId ID de la visita relacionada
   * @param suggestionId ID de la sugerencia evaluada
   * @param feedbackType Tipo de retroalimentaci√≥n (useful, irrelevant, incorrect, dangerous)
   * @param suggestionType Tipo original de la sugerencia
   */
  static logSuggestionFeedback(
    userId: string,
    visitId: string,
    suggestionId: string,
    feedbackType: 'useful' | 'irrelevant' | 'incorrect' | 'dangerous',
    suggestionType: string
  ): void {
    this.log('suggestion_feedback_given', {
      userId,
      visitId,
      patientId: '', // Necesitamos obtener el patientId de alguna manera
      suggestionId,
      suggestion_type: suggestionType,
      feedback_type: feedbackType,
      timestamp: new Date().toISOString()
    });
  }

  static logBlockUpdates(
    oldBlocks: Record<string, unknown>[],
    newBlocks: Record<string, unknown>[],
    userId: string,
    visitId: string
  ) {
    console.log('[AuditLogger] blockUpdate', { oldBlocks, newBlocks, userId, visitId });
  }

  static clearLogs() {
    console.log('[AuditLogger] clearLogs');
  }

  static getAuditLogs(): AuditLogEntry[] {
    return [];
  }

  static getAuditLogsFromSupabase(visitId: string): Promise<AuditLogEntry[]> {
    return Promise.resolve([]);
  }

  public static async logEvent(
    type: string,
    userId: string,
    metadata: Record<string, unknown>,
    visitId?: string
  ): Promise<void> {
    try {
      const { error } = await supabase
        .from('audit_logs')
        .insert({
          type,
          user_id: userId,
          visit_id: visitId,
          metadata,
          created_at: new Date().toISOString()
        });

      if (error) {
        throw error;
      }
    } catch (error) {
      console.error('Error al registrar evento de auditor√≠a:', error);
      throw error;
    }
  }

  public static async getEvents(
    userId?: string,
    visitId?: string,
    type?: string
  ): Promise<AuditEvent[]> {
    try {
      let query = supabase
        .from('audit_logs')
        .select('*')
        .order('created_at', { ascending: false });

      if (userId) {
        query = query.eq('user_id', userId);
      }

      if (visitId) {
        query = query.eq('visit_id', visitId);
      }

      if (type) {
        query = query.eq('type', type);
      }

      const { data, error } = await query;

      if (error) {
        throw error;
      }

      return data.map(event => ({
        id: event.id,
        type: event.type,
        userId: event.user_id,
        visitId: event.visit_id,
        metadata: event.metadata,
        createdAt: new Date(event.created_at)
      }));
    } catch (error) {
      console.error('Error al obtener eventos de auditor√≠a:', error);
      throw error;
    }
  }
}

