import { vi } from "vitest";
import { useState, useEffect } from 'react';
import { Session, AuthChangeEvent, SupabaseClient } from '@supabase/supabase-js';
import supabase from '../auth/supabaseClient';
import { UserProfile, userDataSourceSupabase } from '../services/userDataSourceSupabase';

interface UseSessionResult {
  session: Session | null;
  profile: UserProfile | null;
  loading: boolean;
  error: string | null;
  refreshProfile: () => Promise<void>;
}

/**
 * Hook personalizado para manejar la sesi√≥n de usuario y su perfil
 * - Mantiene la sesi√≥n actualizada
 * - Carga el perfil del usuario con su rol
 * - Proporciona funcionalidades para refrescar los datos
 */
export const useSession = (): UseSessionResult => {
  const [session, setSession] = useState<Session | null>(null);
  const [profile, setProfile] = useState<UserProfile | null>(null);
  const [loading, setLoading] = useState<boolean>(true);
  const [error, setError] = useState<string | null>(null);

  // Carga el perfil del usuario
  const loadUserProfile = async (userId: string) => {
    try {
      const userProfile = await userDataSourceSupabase.getUserProfile(userId);
      setProfile(userProfile);
    } catch (error) {
      console.error('Error loading user profile:', error);
      const err = error as Error;
      setError(err.message);
    }
  };

  // Refresca manualmente el perfil del usuario
  const refreshProfile = async () => {
    if (!session?.user?.id) return;

    try {
      const userProfile = await userDataSourceSupabase.getUserProfile(session.user.id);
      setProfile(userProfile);
    } catch (error) {
      console.error('Error refreshing user profile:', error);
      const err = error as Error;
      setError(err.message);
    }
  };

  useEffect(() => {
    // Obtener la sesi√≥n actual al montar el componente
    const getInitialSession = async () => {
      try {
        setLoading(true);
        const { data, error } = await supabase.auth.getSession();

        if (error) {
          throw error;
        }

        setSession(data.session);

        // Si hay sesi√≥n, cargar el perfil
        if (data.session?.user) {
          await loadUserProfile(data.session.user.id);
        }
      } catch (error) {
        console.error('Error getting session:', error);
        const err = error as Error;
        setError(err.message);
      } finally {
        setLoading(false);
      }
    };

    getInitialSession();

    // Configurar listeners para cambios en la autenticaci√≥n
    const { data: authListener } = supabase.auth.onAuthStateChange(
      async (event: AuthChangeEvent, newSession: Session | null) => {
        setSession(newSession);
        setLoading(true);

        // Evento "SIGNED_IN" o "TOKEN_REFRESHED" - cargar el perfil
        if ((event === 'SIGNED_IN' || event === 'TOKEN_REFRESHED') && newSession?.user) {
          await loadUserProfile(newSession.user.id);
        }

        // Evento "SIGNED_OUT" - limpiar el perfil
        if (event === 'SIGNED_OUT') {
          setProfile(null);
        }

        setLoading(false);
      }
    );

    return () => {
      // Limpiar el listener
      authListener.subscription.unsubscribe();
    };
  }, []);

  return {
    session,
    profile,
    loading,
    error,
    refreshProfile
  };
};

import { vi } from "vitest";
import { AuditLogEntry as BaseAuditLogEntry } from '@/core/audit/AuditLogger';

/**
 * Tipo para representar una entrada de log de auditor√≠a espec√≠fica para MCP
 */
export interface AuditLogEntry extends BaseAuditLogEntry {
  block_id: string;
  block_type: 'contextual' | 'persistent' | 'semantic';
  operation: 'update';
  old_content: string;
  new_content: string;
}

/**
 * Clase que maneja el registro de auditor√≠a para operaciones de MCP
 */
export class AuditLogger {
  private static auditLogs: AuditLogEntry[] = [];

  /**
   * Registra una operaci√≥n de actualizaci√≥n de bloques de memoria
   * @param originalBlocks Bloques originales antes de la modificaci√≥n
   * @param updatedBlocks Bloques despu√©s de la modificaci√≥n
   * @param userId ID del usuario que realiza la operaci√≥n
   * @param visitId ID de la visita asociada
   */
  public static logBlockUpdates(
    originalBlocks: Record<string, unknown>[],
    updatedBlocks: Record<string, unknown>[],
    userId: string,
    visitId: string
  ): void {
    // Crear un mapa de los bloques originales para facilitar la b√∫squeda
    const originalBlocksMap = new Map<string, Record<string, unknown>>();
    originalBlocks.forEach(block => {
      const blockId = block.id as string;
      if (blockId) {
        originalBlocksMap.set(blockId, block);
      }
    });

    // Iterar por los bloques actualizados
    updatedBlocks.forEach(updatedBlock => {
      const blockId = updatedBlock.id as string;
      if (!blockId) return; // Ignorar bloques sin ID

      // Buscar el bloque original correspondiente
      const originalBlock = originalBlocksMap.get(blockId);
      if (!originalBlock) return; // Ignorar si no hay original (ser√≠a creaci√≥n, no actualizaci√≥n)

      // Verificar si el contenido ha cambiado
      const originalContent = originalBlock.content as string;
      const updatedContent = updatedBlock.content as string;

      if (originalContent !== updatedContent) {
        // Registrar la actualizaci√≥n
        const logEntry: AuditLogEntry = {
          id: crypto.randomUUID(),
          timestamp: new Date().toISOString(),
          user_id: userId,
          visit_id: visitId,
          action: 'block.update',
          patient_id: '', // TODO: Obtener el patient_id de alguna manera
          metadata: {
            block_id: blockId,
            block_type: updatedBlock.type,
            old_content: originalContent,
            new_content: updatedContent
          },
          block_id: blockId,
          block_type: updatedBlock.type as 'contextual' | 'persistent' | 'semantic',
          operation: 'update',
          old_content: originalContent,
          new_content: updatedContent
        };

        this.auditLogs.push(logEntry);
      }
    });
  }

  /**
   * Obtiene todos los logs de auditor√≠a registrados (para desarrollo/testing)
   * @returns Array de logs de auditor√≠a
   */
  public static getAuditLogs(): AuditLogEntry[] {
    return [...this.auditLogs];
  }

  /**
   * Limpia todos los logs de auditor√≠a (principalmente para testing)
   */
  public static clearAuditLogs(): void {
    this.auditLogs = [];
  }
}

import { vi } from "vitest";
import { MCPContext, MCPContextSchema } from './schema';

/**
 * Tipo para los datos de memoria utilizados internamente
 */
type MemoryData = {
  source: string;
  data: Array<Record<string, unknown>>;
};

/**
 * Construye un contexto MCP completo combinando diferentes tipos de memoria
 * @param contextualMemory Memoria contextual de la visita
 * @param persistentMemory Memoria persistente del usuario
 * @param semanticMemory Memoria sem√°ntica del usuario
 * @returns Contexto MCP validado
 */
export function buildMCPContext(
  contextualMemory: MemoryData,
  persistentMemory: MemoryData,
  semanticMemory: MemoryData
): MCPContext {
  // Combinar las diferentes memorias en un √∫nico objeto de contexto
  const context = {
    contextual: contextualMemory,
    persistent: persistentMemory,
    semantic: semanticMemory
  };

  // Validar el contexto construido con el esquema definido usando safeParse
  const validationResult = MCPContextSchema.safeParse(context);

  if (!validationResult.success) {
    console.warn("[MCP] Zod validation failed:", validationResult.error.format());
    console.debug("[MCP] Invalid data received:", JSON.stringify(context, null, 2));

    // Intentamos devolver el contexto original aunque tenga errores
    // para evitar p√©rdida silenciosa de datos
    return context as MCPContext;
  }

  return validationResult.data;
}

