// Funci√≥n de ayuda para manejar errores de Supabase y registrar detalles
const handleSupabaseError = (operation: string, error: PostgrestError) => {
  const errorMessage = error?.message || 'Error desconocido';
  const statusCode = error?.code || 'N/A';
  console.error(`Error en operaci√≥n de Supabase [${operation}]: ${errorMessage} (c√≥digo: ${statusCode})`);

  // Registrar detalles adicionales en desarrollo
  if (import.meta.env.DEV) {
    console.error('Detalles completos del error:', error);
  }

  return [];
};

/**
 * Recupera datos de memoria contextual para una visita espec√≠fica desde Supabase
 * @param visitId Identificador √∫nico de la visita
 * @returns Array de bloques de memoria contextual
 */
export async function getContextualMemory(visitId: string): Promise<MCPMemoryBlock[]> {
  try {
    if (!visitId) {
      console.warn('Se intent√≥ recuperar memoria contextual sin proporcionar un ID de visita v√°lido');
      return [];
    }

    // Verificar que supabase est√© inicializado correctamente 
    const client = supabase as SupabaseClient;
    if (!client || !client.from) {
      console.error('Cliente de Supabase no disponible o mal inicializado');
      return [];
    }

    const { data, error } = await client
      .from('contextual_memory')
      .select('*')
      .eq('visit_id', visitId)
      .order('created_at', { ascending: false });

    if (error) {
      return handleSupabaseError('getContextualMemory', error);
    }

    if (!data || data.length === 0) {
      return [];
    }

    // Validar los datos con el esquema Zod - manejo m√°s robusto para tests
    try {
      const validationResult = MCPMemoryBlockSchema.array().safeParse(data);
      if (!validationResult.success) {
        console.error('Error de validaci√≥n en memoria contextual:', validationResult.error);
        return [];
      }
      return validationResult.data;
    } catch (validationError) {
      console.error('Error inesperado durante la validaci√≥n de datos:', validationError);
      return [];
    }
  } catch (error) {
    console.error('Error inesperado al recuperar memoria contextual:', error);
    return [];
  }
}

