/**
 * Actualiza m√∫ltiples bloques de memoria en las tablas correspondientes
 * @param blocks Array de bloques de memoria a actualizar
 * @returns Array con los IDs de los bloques actualizados exitosamente
 */
export async function updateMemoryBlocks(blocks: Record<string, unknown>[]): Promise<string[]> {
  if (!blocks || blocks.length === 0) {
    return [];
  }

  try {
    // Verificar que supabase est√© inicializado correctamente
    const client = supabase as SupabaseClient;
    if (!client || !client.from) {
      console.error('Cliente de Supabase no disponible o mal inicializado');
      return [];
    }

    // Agrupar bloques por tipo para actualizarlos en sus respectivas tablas
    const contextualBlocks = blocks.filter(block => block.type === 'contextual');
    const persistentBlocks = blocks.filter(block => block.type === 'persistent');
    const semanticBlocks = blocks.filter(block => block.type === 'semantic');

    // Funci√≥n para actualizar un bloque en la tabla correspondiente
    const updateBlock = async (block: Record<string, unknown>, tableName: string): Promise<string | null> => {
      try {
        const { id, content, metadata, validated } = block;

        if (!id) return null;

        // Solo actualizamos content, metadata y validated
        // Los dem√°s campos permanecen iguales para mantener la trazabilidad
        const { error } = await client
          .from(tableName)
          .update({
            content,
            metadata,
            validated
          })
          .eq('id', id);

        if (error) {
          console.error(`Error al actualizar bloque ${id} en ${tableName}:`, error.message);
          return null;
        }

        return id as string;
      } catch (err) {
        console.error(`Error inesperado al actualizar bloque en ${tableName}:`, err);
        return null;
      }
    };

    // Actualizar todos los bloques en paralelo
    const updatePromises: Promise<string | null>[] = [
      ...contextualBlocks.map(block => updateBlock(block, 'contextual_memory')),
      ...persistentBlocks.map(block => updateBlock(block, 'persistent_memory')),
      ...semanticBlocks.map(block => updateBlock(block, 'semantic_memory'))
    ];

    const results = await Promise.all(updatePromises);

    // Filtrar resultados nulos (actualizaciones fallidas)
    const updatedIds = results.filter(id => id !== null) as string[];

    return updatedIds;
  } catch (error) {
    console.error('Error al actualizar bloques de memoria:', error);
    return [];
  }
}

import { vi } from "vitest";
import * as SupabaseDataSource from './MCPDataSourceSupabase';
import { MCPMemoryBlock } from './schema';

export class MCPManager {
  async getContextForVisit(visitId: string): Promise<any> {
    return { context: 'mocked context for visitId ' + visitId };
  }

  async saveContext(visitId: string, context: any) {
    console.log(`[MCPManager] Context saved for ${visitId}`, context);
  }

  /**
   * Construye un contexto completo a partir de datos en Supabase
   * @param visitId ID de la visita para obtener memoria contextual
   * @param patientId ID del paciente para obtener memoria persistente
   * @returns Contexto estructurado con datos de supabase
   */
  async buildContext(visitId: string, patientId: string) {
    try {
      // Intentar obtener datos de memoria contextual
      // Si falla, devolver estructura base con arrays vac√≠os
      const contextualPromise = SupabaseDataSource.getContextualMemory(visitId);

      try {
        await contextualPromise;
      } catch (error) {
        console.error('[MCPManager] Error al obtener memoria contextual:', error);
        // Si falla la memoria contextual, devolvemos un contexto vac√≠o
        return {
          contextual: { source: 'supabase', data: [] },
          persistent: { source: 'supabase', data: [] },
          semantic: { source: 'supabase', data: [] }
        };
      }

      // Si llegamos aqu√≠, la memoria contextual fue exitosa
      // Obtener el resto de datos
      const [contextualMemory, persistentMemory, semanticMemory] = await Promise.all([
        contextualPromise,
        SupabaseDataSource.getPersistentMemory(patientId).catch(() => []),
        SupabaseDataSource.getSemanticMemory().catch(() => [])
      ]);

      // Procesar los bloques de memoria
      const processBlocks = (blocks: MCPMemoryBlock[] = []) => 
        blocks.map(block => ({
          id: block.id,
          created_at: block.created_at,
          type: block.type,
          content: block.content,
          timestamp: block.created_at,
        }));

      // Construir el objeto de contexto final
      return {
        contextual: { 
          source: 'supabase', 
          data: processBlocks(contextualMemory) 
        },
        persistent: { 
          source: 'supabase', 
          data: processBlocks(persistentMemory) 
        },
        semantic: { 
          source: 'supabase', 
          data: processBlocks(semanticMemory) 
        }
      };
    } catch (error) {
      console.error('[MCPManager] Error inesperado al construir contexto:', error);
      // En caso de error, devolver estructura base con arrays vac√≠os
      return {
        contextual: { source: 'supabase', data: [] },
        persistent: { source: 'supabase', data: [] },
        semantic: { source: 'supabase', data: [] }
      };
    }
  }
}

