import React from 'react';

export interface ClinicalFilters {
  selectedFields: string[];
  hasSummary: boolean;
  dateRange: {
    from: string;
    to: string;
  };
}

interface ClinicalFiltersProps {
  filters: ClinicalFilters;
  onFiltersChange: (filters: ClinicalFilters) => void;
  totalVisits: number;
  filteredVisits: number;
}

const AVAILABLE_FIELDS = [
  { id: 'anamnesis', label: 'Anamnesis' },
  { id: 'diagnostico', label: 'Diagn√≥stico' },
  { id: 'plan', label: 'Plan' },
  { id: 'evolucion', label: 'Evoluci√≥n' }
];

const ClinicalFilters: React.FC<ClinicalFiltersProps> = ({
  filters,
  onFiltersChange,
  totalVisits,
  filteredVisits
}) => {
  const handleFieldToggle = (fieldId: string) => {
    const newFields = filters.selectedFields.includes(fieldId)
      ? filters.selectedFields.filter(f => f !== fieldId)
      : [...filters.selectedFields, fieldId];

    onFiltersChange({
      ...filters,
      selectedFields: newFields
    });
  };

  const handleSummaryToggle = () => {
    onFiltersChange({
      ...filters,
      hasSummary: !filters.hasSummary
    });
  };

  const handleDateChange = (type: 'from' | 'to', value: string) => {
    onFiltersChange({
      ...filters,
      dateRange: {
        ...filters.dateRange,
        [type]: value
      }
    });
  };

  return (
    <div className="bg-white rounded-lg shadow-sm p-4 mb-4">
      <div className="flex justify-between items-center mb-4">
        <h3 className="text-lg font-medium text-gray-900">
          Filtros Cl√≠nicos
        </h3>
        <span className="text-sm text-gray-600">
          Mostrando {filteredVisits} de {totalVisits} visitas
        </span>
      </div>

      <div className="space-y-4">
        {/* Campos cl√≠nicos */}
        <div>
          <label className="block text-sm font-medium text-gray-700 mb-2">
            Campos Impactados por IA
          </label>
          <div className="flex flex-wrap gap-2">
            {AVAILABLE_FIELDS.map(field => (
              <button
                key={field.id}
                onClick={() => handleFieldToggle(field.id)}
                className={`px-3 py-1 rounded-full text-sm font-medium transition-colors
                  ${filters.selectedFields.includes(field.id)
                    ? 'bg-blue-100 text-blue-800'
                    : 'bg-gray-100 text-gray-600 hover:bg-gray-200'
                  }`}
              >
                {field.label}
              </button>
            ))}
          </div>
        </div>

        {/* Resumen IA */}
        <div className="flex items-center">
          <input
            type="checkbox"
            id="hasSummary"
            checked={filters.hasSummary}
            onChange={handleSummaryToggle}
            className="h-4 w-4 text-blue-600 rounded border-gray-300"
          />
          <label htmlFor="hasSummary" className="ml-2 text-sm text-gray-700">
            Solo visitas con resumen cl√≠nico generado
          </label>
        </div>

        {/* Rango de fechas */}
        <div className="grid grid-cols-2 gap-4">
          <div>
            <label htmlFor="dateFrom" className="block text-sm font-medium text-gray-700 mb-1">
              Desde
            </label>
            <input
              type="date"
              id="dateFrom"
              value={filters.dateRange.from}
              onChange={(e) => handleDateChange('from', e.target.value)}
              className="w-full rounded-md border-gray-300 shadow-sm focus:border-blue-500 focus:ring-blue-500"
            />
          </div>
          <div>
            <label htmlFor="dateTo" className="block text-sm font-medium text-gray-700 mb-1">
              Hasta
            </label>
            <input
              type="date"
              id="dateTo"
              value={filters.dateRange.to}
              onChange={(e) => handleDateChange('to', e.target.value)}
              className="w-full rounded-md border-gray-300 shadow-sm focus:border-blue-500 focus:ring-blue-500"
            />
          </div>
        </div>
      </div>
    </div>
  );
};

export default ClinicalFilters;

import React from 'react';

interface SuggestionLog {
  visit_id: string;
  content: string;
  field: string;
  accepted_by: string;
  accepted_at: string;
}

interface VisitIndicatorsProps {
  suggestions: SuggestionLog[];
  visitId: string;
}

const VisitIndicators: React.FC<VisitIndicatorsProps> = ({ suggestions, visitId }) => {
  // Calcular total de sugerencias
  const totalSuggestions = suggestions.length;

  // Calcular campos m√°s impactados
  const fieldCounts = suggestions.reduce((acc, suggestion) => {
    acc[suggestion.field] = (acc[suggestion.field] || 0) + 1;
    return acc;
  }, {} as Record<string, number>);

  // Obtener los 2 campos m√°s frecuentes
  const topFields = Object.entries(fieldCounts)
    .sort(([, a], [, b]) => b - a)
    .slice(0, 2)
    .map(([field]) => field);

  // Simular valores de evoluci√≥n (esto ser√≠a reemplazado por datos reales)
  const expectedImprovement = 75;
  const actualImprovement = 60;

  return (
    <div className="mt-4 pt-4 border-t border-gray-200">
      <h4 className="text-sm font-medium text-gray-700 mb-3">
        Indicadores de Impacto IA
      </h4>

      <div className="space-y-3">
        {/* Total de sugerencias */}
        <div className="flex items-center space-x-2">
          <span className="inline-flex items-center px-2.5 py-0.5 rounded-full text-xs font-medium bg-blue-100 text-blue-800">
            {totalSuggestions} sugerencias integradas
          </span>
        </div>

        {/* Campos m√°s impactados */}
        {topFields.length > 0 && (
          <div className="flex flex-wrap gap-2">
            {topFields.map(field => (
              <span
                key={field}
                className="inline-flex items-center px-2.5 py-0.5 rounded-full text-xs font-medium bg-green-100 text-green-800"
              >
                {field}: {fieldCounts[field]} sugerencias
              </span>
            ))}
          </div>
        )}

        {/* Barra de progreso de evoluci√≥n */}
        <div className="space-y-1">
          <div className="flex justify-between text-xs text-gray-600">
            <span>Evoluci√≥n Cl√≠nica</span>
            <span>{actualImprovement}% vs {expectedImprovement}% esperado</span>
          </div>
          <div className="w-full bg-gray-200 rounded-full h-2">
            <div
              className="bg-blue-600 h-2 rounded-full"
              style={{ width: `${actualImprovement}%` }}
            />
          </div>
          <div className="w-full bg-gray-200 rounded-full h-2 mt-1">
            <div
              className="bg-gray-400 h-2 rounded-full"
              style={{ width: `${expectedImprovement}%` }}
            />
          </div>
          <div className="flex justify-between text-xs text-gray-500">
            <span>Real</span>
            <span>Esperado</span>
          </div>
        </div>
      </div>
    </div>
  );
};

