import { vi } from "vitest";
import React from 'react';
import { render, screen, fireEvent, waitFor } from '@testing-library/react';
import { describe, it, expect, beforeEach, afterEach } from 'vitest';
import '@testing-library/jest-dom';
import AgentSuggestionExplainer from '../AgentSuggestionExplainer';
import { AgentSuggestion, SuggestionType, SuggestionField } from '../../../../types/agent';
import * as AgentExplainer from '../../../../core/agent/AgentExplainer';

// Mock de la funci√≥n explainSuggestion
vi.mock('../../../../core/agent/AgentExplainer', () => ({
  explainSuggestion: vi.fn()
}));

describe('AgentSuggestionExplainer', () => {
  // Resetear todos los mocks despu√©s de cada prueba
  afterEach(() => {
    vi.resetAllMocks();
  });

  beforeEach(() => {
    // Configuraci√≥n por defecto del mock
    vi.mocked(AgentExplainer.explainSuggestion).mockResolvedValue(
      'Explicaci√≥n simulada para pruebas'
    );
  });

  // Datos de prueba con tipos correctos
  const mockRecommendation: AgentSuggestion = {
    id: 'sugg-1',
    sourceBlockId: 'block-1',
    type: 'recommendation',
    field: 'diagnosis',
    content: 'Considerar evaluaci√≥n de escala de dolor.',
    createdAt: new Date(),
    updatedAt: new Date()
  };

  const mockWarning: AgentSuggestion = {
    id: 'sugg-2',
    sourceBlockId: 'block-2',
    type: 'warning',
    field: 'medication',
    content: 'Monitorizar tensi√≥n arterial cada 4 horas.',
    createdAt: new Date(),
    updatedAt: new Date()
  };

  const mockInfo: AgentSuggestion = {
    id: 'sugg-3',
    sourceBlockId: 'block-3',
    type: 'info',
    field: 'history',
    content: 'Paciente con historial de diabetes.',
    createdAt: new Date(),
    updatedAt: new Date()
  };

  it('renderiza el bot√≥n de explicaci√≥n para sugerencias de tipo recommendation', () => {
    render(<AgentSuggestionExplainer suggestion={mockRecommendation} />);

    // Verificar que el bot√≥n est√° en el documento
    const button = screen.getByRole('button', { name: /Ver explicaci√≥n/i });
    expect(button).toBeInTheDocument();
  });

  it('renderiza el bot√≥n de explicaci√≥n para sugerencias de tipo warning', () => {
    render(<AgentSuggestionExplainer suggestion={mockWarning} />);

    // Verificar que el bot√≥n est√° en el documento
    const button = screen.getByRole('button', { name: /Ver explicaci√≥n/i });
    expect(button).toBeInTheDocument();
  });

  it('no renderiza nada para sugerencias de tipo info', () => {
    const { container } = render(<AgentSuggestionExplainer suggestion={mockInfo} />);

    // Verificar que el contenedor est√° vac√≠o
    expect(container).toBeEmptyDOMElement();
  });

  it('muestra indicador de carga al solicitar una explicaci√≥n', async () => {
    // Configurar el mock para que tarde en resolver
    vi.mocked(AgentExplainer.explainSuggestion).mockImplementation(() => {
      return new Promise<string>((resolve) => {
        setTimeout(() => {
          resolve('Explicaci√≥n simulada retrasada');
        }, 100);
      });
    });

    render(<AgentSuggestionExplainer suggestion={mockRecommendation} />);

    // Hacer clic en el bot√≥n de explicaci√≥n
    fireEvent.click(screen.getByRole('button', { name: /Ver explicaci√≥n/i }));

    // Verificar que se muestra el indicador de carga
    expect(screen.getByTestId('loading-indicator')).toBeInTheDocument();

    // Esperar a que se complete la carga
    await waitFor(() => {
      expect(screen.queryByTestId('loading-indicator')).not.toBeInTheDocument();
    });
  });

  it('muestra la explicaci√≥n despu√©s de hacer clic en el bot√≥n', async () => {
    // Configurar respuesta espec√≠fica para este test
    const explicacionEsperada = 'Esta es una explicaci√≥n detallada de la recomendaci√≥n';
    vi.mocked(AgentExplainer.explainSuggestion).mockResolvedValueOnce(explicacionEsperada);

    render(<AgentSuggestionExplainer suggestion={mockRecommendation} />);

    // Verificar que la explicaci√≥n no est√° visible inicialmente
    expect(screen.queryByTestId('explanation-text')).not.toBeInTheDocument();

    // Hacer clic en el bot√≥n de explicaci√≥n
    fireEvent.click(screen.getByRole('button', { name: /Ver explicaci√≥n/i }));

    // Verificar que la explicaci√≥n se muestra despu√©s de cargar
    await waitFor(() => {
      expect(screen.getByTestId('explanation-text')).toBeInTheDocument();
      expect(screen.getByText(explicacionEsperada)).toBeInTheDocument();
    });

    // Verificar que el bot√≥n cambi√≥ a "Ocultar explicaci√≥n"
    expect(screen.getByRole('button', { name: /Ocultar explicaci√≥n/i })).toBeInTheDocument();
  });

  it('oculta la explicaci√≥n al hacer clic en Ocultar explicaci√≥n', async () => {
    render(<AgentSuggestionExplainer suggestion={mockRecommendation} />);

    // Mostrar la explicaci√≥n
    fireEvent.click(screen.getByRole('button', { name: /Ver explicaci√≥n/i }));

    // Esperar a que se cargue la explicaci√≥n
    await waitFor(() => {
      expect(screen.getByTestId('explanation-text')).toBeInTheDocument();
    });

    // Ocultar la explicaci√≥n
    fireEvent.click(screen.getByRole('button', { name: /Ocultar explicaci√≥n/i }));

    // Verificar que la explicaci√≥n ya no est√° visible
    expect(screen.queryByTestId('explanation-text')).not.toBeInTheDocument();
  });

  it('no vuelve a llamar a explainSuggestion si ya se gener√≥ una explicaci√≥n', async () => {
    render(<AgentSuggestionExplainer suggestion={mockRecommendation} />);

    // Primera vez que se hace clic para mostrar la explicaci√≥n
    fireEvent.click(screen.getByRole('button', { name: /Ver explicaci√≥n/i }));

    // Esperar a que se cargue la explicaci√≥n
    await waitFor(() => {
      expect(screen.getByTestId('explanation-text')).toBeInTheDocument();
    });

    // Ocultar la explicaci√≥n
    fireEvent.click(screen.getByRole('button', { name: /Ocultar explicaci√≥n/i }));

    // Volver a mostrar la explicaci√≥n
    fireEvent.click(screen.getByRole('button', { name: /Ver explicaci√≥n/i }));

    // Verificar que explainSuggestion solo se llam√≥ una vez
    expect(AgentExplainer.explainSuggestion).toHaveBeenCalledTimes(1);
  });

  it('maneja los errores cuando falla la generaci√≥n de explicaci√≥n', async () => {
    // Configurar el mock para simular un error
    vi.mocked(AgentExplainer.explainSuggestion).mockRejectedValueOnce(new Error('Error simulado'));

    render(<AgentSuggestionExplainer suggestion={mockWarning} />);

    // Hacer clic en el bot√≥n de explicaci√≥n
    fireEvent.click(screen.getByRole('button', { name: /Ver explicaci√≥n/i }));

    // Esperar a que aparezca el mensaje de error
    await waitFor(() => {
      const errorElement = screen.getByTestId('explanation-text');
      expect(errorElement).toBeInTheDocument();
      expect(errorElement).toHaveTextContent('No se pudo generar una explicaci√≥n para esta sugerencia.');
      expect(errorElement).toHaveClass('text-red-600');
      expect(errorElement).toHaveClass('bg-red-50');
