      patient_id: 'patient-001',
      visit_id: 'visit-456',
      timestamp: '2025-05-20T14:40:00.000Z',
      metadata: {
        visit_id: 'visit-456',
        block_id: 'block-3',
        block_type: 'semantic',
        operation: 'update',
        old_content: 'Contenido que no deber√≠a aparecer',
        new_content: 'Contenido modificado que no deber√≠a aparecer'
