    expect(screen.getByText('Fecha y Hora')).toBeInTheDocument();
    expect(screen.getByText('Usuario')).toBeInTheDocument();
    expect(screen.getByText('Tipo')).toBeInTheDocument();
    expect(screen.getByText('Detalles')).toBeInTheDocument();
  });

  it('filtra los logs que no corresponden a la visita actual', () => {
    render(<AuditLogViewer visitId={mockVisitId} logs={mockLogs} />);
    fireEvent.click(screen.getByText('Mostrar historial'));

    expect(screen.getByText(/Contenido original para pruebas/)).toBeInTheDocument();
    expect(screen.getByText(/Otro contenido original/)).toBeInTheDocument();
    expect(screen.queryByText(/Contenido que no deber√≠a aparecer/)).not.toBeInTheDocument();
    expect(screen.getByText('Total de registros: 2')).toBeInTheDocument();
  });

  it('muestra los tipos de bloques con nombres amigables', () => {
    render(<AuditLogViewer visitId={mockVisitId} logs={mockLogs} />);
    fireEvent.click(screen.getByText('Mostrar historial'));

    expect(screen.getByText(/Memoria Contextual/)).toBeInTheDocument();
    expect(screen.getByText(/Memoria Persistente/)).toBeInTheDocument();
  });

  it('colapsa la tabla al hacer clic en Ocultar historial', () => {
    render(<AuditLogViewer visitId={mockVisitId} logs={mockLogs} />);
    fireEvent.click(screen.getByText('Mostrar historial'));
    expect(screen.getByText('Fecha y Hora')).toBeInTheDocument();

    fireEvent.click(screen.getByText('Ocultar historial'));
    expect(screen.queryByText('Fecha y Hora')).not.toBeInTheDocument();
  });
});

import { useState, useEffect } from 'react';
import supabase from '@/core/auth/supabaseClient';
import { testDirectConnection } from '@/core/auth/directClient';
import { SupabaseClient } from '@supabase/supabase-js';

interface ErrorWithMessage {
  message?: string;
  hint?: string;
}

// Componente simplificado para comprobar la conexi√≥n a Supabase
export const ConnectionStatus = () => {
  const [status, setStatus] = useState<'checking' | 'connected' | 'error'>('checking');
  const [errorInfo, setErrorInfo] = useState<string | null>(null);
  const [details, setDetails] = useState<string | null>(null);

  useEffect(() => {
    // Funci√≥n para comprobar la conexi√≥n con varios m√©todos
    const checkConnection = async () => {
      console.log('Iniciando diagn√≥stico de conexi√≥n a Supabase...');

      try {
        // 1. Primero intentar con el m√©todo directo con fetch
        console.log('M√âTODO 1: Probando conexi√≥n directa con fetch...');
        const directResult = await testDirectConnection();

        if (directResult.success) {
          setStatus('connected');
          setErrorInfo(null);
          setDetails('‚úÖ Conexi√≥n directa con fetch exitosa');
          console.log('‚úÖ M√©todo directo exitoso');
          return;
        } 

        console.log('‚ùå M√©todo directo fall√≥:', directResult.error);

        // 2. Si fall√≥ el m√©todo directo, intentar con el cliente oficial
        console.log('M√âTODO 2: Probando con el cliente oficial de Supabase...');
        const client = supabase as SupabaseClient;
        if (!client) {
          throw new Error('Cliente Supabase no disponible');
        }

        // Lista de tablas para probar
        const tablesToTry = ['health_check', 'contextual_memory', 'persistent_memory'];
        let successfulTable = null;
        let lastError: ErrorWithMessage | null = null;

        // Probar cada tabla
        for (const table of tablesToTry) {
          try {
            console.log(`Probando tabla ${table} con cliente oficial...`);
            const { data, error } = await client
              .from(table)
              .select('*')
              .limit(1);

            if (error) {
              console.error(`Error en tabla ${table}:`, error);
              lastError = error;
            } else {
              console.log(`‚úÖ Conexi√≥n exitosa con tabla ${table}`);
              successfulTable = table;
              break;
            }
          } catch (err) {
            console.error(`Error inesperado en tabla ${table}:`, err);
            lastError = err as ErrorWithMessage;
          }
        }

        // Evaluar resultados del segundo m√©todo
        if (successfulTable) {
          setStatus('connected');
          setErrorInfo(null);
          setDetails(`Conexi√≥n establecida con cliente oficial a trav√©s de tabla: ${successfulTable}`);
        } else {
          // Si ambos m√©todos fallaron, mostrar error del m√©todo directo por ser m√°s ilustrativo
          setStatus('error');

          if (directResult.error) {
            const directError = directResult.error as ErrorWithMessage;
            const message = directError.message || 'Error desconocido';
            const hint = directError.hint || '';
            setErrorInfo(`${message}${hint ? ` (${hint})` : ''}`);
          } else if (lastError) {
            const message = lastError.message || 'Error desconocido';
            setErrorInfo(message);
          } else {
            setErrorInfo('No se pudo conectar a Supabase por ning√∫n m√©todo');
          }

          // Detalles t√©cnicos completos
          setDetails(JSON.stringify({ 
            directMethod: directResult, 
            clientMethod: { error: lastError } 
          }, null, 2));
        }
      } catch (err) {
        console.error('Error general al verificar conexi√≥n:', err);
        setStatus('error');
        setErrorInfo(err instanceof Error ? err.message : 'Error desconocido');
      }
    };

    // Ejecutar verificaci√≥n
    checkConnection();

  }, []);

  return (
    <div className="connection-status p-4 border rounded">
      <h2 className="text-lg font-bold mb-2">Estado de conexi√≥n a Supabase</h2>

      {status === 'checking' && (
        <div className="checking">
          <p className="text-blue-600">‚åõ Comprobando conexi√≥n con Supabase...</p>
        </div>
      )}

      {status === 'connected' && (
        <div className="connected">
          <p className="text-green-600">‚úÖ Conexi√≥n a Supabase activa</p>
          {details && <p className="text-sm text-gray-600">{details}</p>}
        </div>
      )}

      {status === 'error' && (
        <div className="error">
          <p className="text-red-600">‚ùå Error en la conexi√≥n a Supabase</p>
          {errorInfo && <p className="text-sm text-red-500">{errorInfo}</p>}

          <div className="mt-4 bg-gray-100 p-3 rounded text-xs">
            <p className="font-medium text-gray-700">Posibles soluciones:</p>
            <ul className="list-disc pl-4 mt-1 space-y-1">
              <li>Verificar que la API key en el proyecto sea correcta y est√© activa</li>
              <li>Comprobar que no hay problemas de CORS en la configuraci√≥n de Supabase</li>
              <li>Intentar generar una nueva API key en el panel de control de Supabase</li>
            </ul>
          </div>

          {details && (
            <details className="mt-4">
              <summary className="cursor-pointer text-sm text-blue-500">Ver detalles t√©cnicos</summary>
              <pre className="text-xs bg-gray-100 p-2 mt-1 overflow-auto max-h-40">{details}</pre>
            </details>
          )}

          <button 
            onClick={() => window.location.reload()}
            className="mt-4 px-3 py-1 bg-blue-500 text-white rounded hover:bg-blue-600"
          >
            Reintentar
          </button>
        </div>
      )}
    </div>
  );
};

