      {block.tags && block.tags.length > 0 && (
        <div className="mt-2 flex flex-wrap gap-1">
          {block.tags.map((tag, index) => (
            <span key={index} className="text-xs px-2 py-0.5 bg-gray-100 rounded-full">
              {tag}
            </span>
          ))}
