  );
};

/**
 * Componente para mostrar una secci√≥n de memoria (contextual, persistent o semantic)
 */
const MemorySection: React.FC<{
  title: string;
  source: string;
  data: unknown[];
  colorClass: string;
  editable: boolean;
  onValidateBlock: (id: string, validated: boolean) => void;
