import { vi } from "vitest";
import React, { useState, useEffect } from 'react';
import { format } from 'date-fns';
import { es } from 'date-fns/locale';
import { 
  LongitudinalMetric, 
  getEvolutionIndicator, 
  getLongitudinalMetricsByPatient, 
  getLongitudinalMetricForVisit 
} from '../../../services/UsageAnalyticsService';

/**
 * Props para el componente LongitudinalMetricsViewer
 */
interface LongitudinalMetricsViewerProps {
  patientId?: string;
  visitId?: string;
  compactView?: boolean;
}

/**
 * Interfaz para tipar los detalles de m√©tricas previas
 */
interface PreviousMetricsDetails {
  generated: number;
  accepted: number;
  integrated: number;
  field_matched: number;
  warnings: number;
  estimated_time_saved_minutes: number;
}

/**
 * Interfaz para tipar los detalles de m√©tricas longitudinales
 */
interface MetricDetails {
  previous_metrics: PreviousMetricsDetails;
  current_metrics: PreviousMetricsDetails;
  comparison_date: string;
}

/**
 * Funci√≥n para verificar si un objeto tiene el formato de MetricDetails
 */
function isMetricDetails(details: unknown): details is MetricDetails {
  if (!details || typeof details !== 'object') return false;

  const d = details as Record<string, unknown>;

  return (
    'previous_metrics' in d && 
    'current_metrics' in d && 
    'comparison_date' in d &&
    typeof d.previous_metrics === 'object' &&
    typeof d.current_metrics === 'object' &&
    typeof d.comparison_date === 'string'
  );
}

/**
 * Funci√≥n para obtener de forma segura detalles de m√©tricas
 */
function getPreviousMetric(details: unknown, key: keyof PreviousMetricsDetails, defaultValue = 0): number {
  if (!isMetricDetails(details)) return defaultValue;

  const value = details.previous_metrics[key];
  return typeof value === 'number' ? value : defaultValue;
}

/**
 * Componente que muestra m√©tricas longitudinales de evoluci√≥n cl√≠nica
 */
const LongitudinalMetricsViewer: React.FC<LongitudinalMetricsViewerProps> = ({ 
  patientId, 
  visitId,
  compactView = false
}) => {
  // Estados
  const [metrics, setMetrics] = useState<LongitudinalMetric[]>([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState<string | null>(null);
  const [selectedMetric, setSelectedMetric] = useState<LongitudinalMetric | null>(null);
  const [expandedView, setExpandedView] = useState(false);

  // Cargar m√©tricas al montar el componente
  useEffect(() => {
    const loadMetrics = async () => {
      try {
        setLoading(true);
        setError(null);

        // Si tenemos un visitId espec√≠fico, cargar solo esa m√©trica
        if (visitId) {
          const metric = await getLongitudinalMetricForVisit(visitId);
          if (metric) {
            setMetrics([metric]);
            setSelectedMetric(metric);
          } else {
            setMetrics([]);
            setSelectedMetric(null);
          }
        } 
        // Si tenemos un patientId, cargar todas las m√©tricas del paciente
        else if (patientId) {
          const patientMetrics = await getLongitudinalMetricsByPatient(patientId);
          setMetrics(patientMetrics);

          if (patientMetrics.length > 0) {
            setSelectedMetric(patientMetrics[0]);
          }
        } else {
          setError('Se requiere un patientId o visitId para mostrar m√©tricas');
        }
      } catch (err) {
        console.error('Error al cargar m√©tricas longitudinales:', err);
        setError('Error al cargar m√©tricas de evoluci√≥n cl√≠nica');
      } finally {
        setLoading(false);
      }
    };

    loadMetrics();
  }, [patientId, visitId]);

  // Si no hay m√©tricas, no mostrar nada
  if (!loading && metrics.length === 0) {
    if (compactView) {
      return null;
    }

    return (
      <div className="bg-gray-50 p-4 rounded-md border border-gray-200 text-center my-4">
        <p className="text-gray-500">No hay m√©tricas longitudinales disponibles</p>
        <p className="text-sm text-gray-400 mt-1">Se requieren al menos dos visitas para generar m√©tricas comparativas</p>
      </div>
    );
  }

  // Formatear fecha para mostrar
  const formatDate = (dateString: string): string => {
    try {
      return format(new Date(dateString), "dd 'de' MMMM 'de' yyyy", { locale: es });
    } catch (e) {
      return dateString;
    }
  };

  // El c√°lculo de variaci√≥n porcentual se hac√≠a directamente en renderVariation

  // Obtener clase CSS seg√∫n la evoluci√≥n cl√≠nica
  const getEvolutionClass = (evolution: 'improved' | 'stable' | 'worsened'): string => {
    switch (evolution) {
      case 'improved': return 'text-green-600';
      case 'stable': return 'text-yellow-600';
      case 'worsened': return 'text-red-600';
      default: return 'text-gray-600';
    }
  };

  // Mostrar texto seg√∫n la evoluci√≥n cl√≠nica
  const getEvolutionText = (evolution: 'improved' | 'stable' | 'worsened'): string => {
    switch (evolution) {
      case 'improved': return 'Mejor√≠a';
      case 'stable': return 'Estable';
      case 'worsened': return 'Empeoramiento';
      default: return 'Desconocido';
    }
  };

  // Vista compacta (para uso en tarjetas o paneles peque√±os)
  if (compactView && selectedMetric) {
    return (
      <div className="border rounded-md border-gray-200 bg-white p-3 shadow-sm">
        <h3 className="text-sm font-medium text-gray-700 flex items-center mb-2">
          <svg xmlns="http://www.w3.org/2000/svg" viewBox="0 0 20 20" fill="currentColor" className="w-4 h-4 mr-1 text-blue-500">
            <path d="M15.5 2A1.5 1.5 0 0014 3.5v13a1.5 1.5 0 001.5 1.5h1a1.5 1.5 0 001.5-1.5v-13A1.5 1.5 0 0016.5 2h-1zM9.5 6A1.5 1.5 0 008 7.5v9A1.5 1.5 0 009.5 18h1a1.5 1.5 0 001.5-1.5v-9A1.5 1.5 0 0010.5 6h-1zM3.5 10A1.5 1.5 0 002 11.5v5A1.5 1.5 0 003.5 18h1A1.5 1.5 0 006 16.5v-5A1.5 1.5 0 004.5 10h-1z" />
          </svg>
          Evoluci√≥n Cl√≠nica
        </h3>

        <div className="flex items-center mb-2">
          <div className="text-2xl mr-2">
            {getEvolutionIndicator(selectedMetric.clinical_evolution)}
          </div>
          <div className={`font-medium ${getEvolutionClass(selectedMetric.clinical_evolution)}`}>
            {getEvolutionText(selectedMetric.clinical_evolution)}
          </div>
        </div>

        <div className="text-xs text-gray-500">
          Comparaci√≥n con visita anterior: {formatDate(selectedMetric.date)}
        </div>
      </div>
    );
  }

  // Vista detallada
  return (
    <div className="border rounded-md border-gray-200 bg-white shadow-sm mt-6 mb-6">
      <div className="p-3 border-b border-gray-200 bg-gray-50 flex justify-between items-center">
