import { useState, FormEvent, useEffect } from 'react';
import { Link, useNavigate, useLocation } from 'react-router-dom';
import { userDataSourceSupabase } from '../core/services/userDataSourceSupabase';
import { useUser } from '../core/auth/UserContext';
import { checkSupabaseConnection } from '../utils/checkSupabaseConnection';
import { SUPABASE_URL } from '../config/env';

const LoginPage = () => {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState<string | null>(null);
  const [connectionStatus, setConnectionStatus] = useState<'checking' | 'ok' | 'error'>('checking');
  const navigate = useNavigate();
  const location = useLocation();
  const { user } = useUser();

  // Si el usuario ya est√° autenticado, redirigir a la p√°gina principal
  useEffect(() => {
    if (user) {
      navigate('/');
    }
  }, [user, navigate]);

  // Comprobar la conexi√≥n a Supabase al cargar
  useEffect(() => {
    const checkConnection = async () => {
      try {
        const result = await checkSupabaseConnection();
        setConnectionStatus(result.isConnected ? 'ok' : 'error');
        if (!result.isConnected) {
          console.error('Error de conexi√≥n a Supabase:', result.error);
        }
      } catch (err) {
        setConnectionStatus('error');
        console.error('Error comprobando la conexi√≥n:', err);
      }
    };

    checkConnection();
  }, []);

  // Si viene de una ruta protegida, obtener la URL original
  const from = location.state?.from?.pathname || '/';

  const handleLogin = async (e: FormEvent) => {
    e.preventDefault();

    if (!email || !password) {
      setError('Por favor, completa todos los campos');
      return;
    }

    // Si ya sabemos que hay problema de conexi√≥n, mostrar mensaje espec√≠fico
    if (connectionStatus === 'error') {
      setError(`Error de conexi√≥n con el servidor (${SUPABASE_URL}). Por favor, verifica tu conexi√≥n a internet o contacta al administrador.`);
      return;
    }

    try {
      setLoading(true);
      setError(null);

      // Usar el servicio de usuarios para autenticar
      const data = await userDataSourceSupabase.signInWithPassword(email, password);

      if (data?.user) {
        // Redirigir a la p√°gina original o a la principal
        navigate(from, { replace: true });
      }
    } catch (error: unknown) {
      const err = error as Error;

      // Mostrar mensajes m√°s amigables seg√∫n el error
      if (err.message.includes('Failed to fetch') || err.message.includes('NetworkError')) {
        setError(`Error de conexi√≥n con el servidor. Por favor, verifica tu conexi√≥n a internet.`);
      } else if (err.message.includes('Invalid login')) {
        setError('Credenciales incorrectas. Por favor, verifica tu email y contrase√±a.');
      } else {
      setError(err.message || 'Error al iniciar sesi√≥n');
      }

      console.error('Error al iniciar sesi√≥n:', err.message);
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="min-h-screen flex items-center justify-center bg-boneWhite px-4 sm:px-6 lg:px-8 font-sans">
      <div className="w-full max-w-md space-y-8">
        <div className="text-center">
          <h1 className="text-3xl font-bold tracking-tight text-slateBlue">AiDuxCare</h1>
          <h2 className="mt-6 text-2xl font-bold tracking-tight text-slateBlue">Iniciar sesi√≥n</h2>
          <p className="mt-2 text-sm text-slateBlue/70">
            Accede a tu cuenta para gestionar pacientes y consultas
          </p>
        </div>

        {error && (
          <div className="bg-softCoral/10 border-l-4 border-softCoral p-4">
            <p className="text-softCoral">{error}</p>
          </div>
        )}

        <form className="mt-8 space-y-6" onSubmit={handleLogin} role="form">
          <div className="space-y-4 rounded-md shadow-sm">
            <div>
              <label htmlFor="email-address" className="sr-only">
                Correo electr√≥nico
              </label>
              <input
                id="email-address"
                name="email"
                type="email"
                autoComplete="email"
                required
                className="relative block w-full rounded-md border border-neutralGray p-3 text-slateBlue placeholder:text-neutralGray focus:ring-2 focus:ring-intersectionGreen focus:border-intersectionGreen sm:text-sm"
                placeholder="Correo electr√≥nico"
                value={email}
                onChange={(e) => setEmail(e.target.value)}
              />
            </div>
            <div>
              <label htmlFor="password" className="sr-only">
                Contrase√±a
              </label>
              <input
                id="password"
                name="password"
                type="password"
                autoComplete="current-password"
                required
                className="relative block w-full rounded-md border border-neutralGray p-3 text-slateBlue placeholder:text-neutralGray focus:ring-2 focus:ring-intersectionGreen focus:border-intersectionGreen sm:text-sm"
                placeholder="Contrase√±a"
                value={password}
                onChange={(e) => setPassword(e.target.value)}
              />
            </div>
          </div>

          <div>
            <button
              type="submit"
              disabled={loading}
              className={`group relative flex w-full justify-center rounded-md bg-softCoral px-3 py-3 text-sm font-semibold text-white hover:bg-intersectionGreen focus:outline-none focus:ring-2 focus:ring-intersectionGreen focus:ring-offset-2 transition-colors ${loading ? 'opacity-70 cursor-not-allowed' : ''}`}
            >
              {loading ? 'Iniciando sesi√≥n...' : 'Iniciar sesi√≥n'}
            </button>
          </div>
        </form>

        <div className="text-center mt-4">
          <p className="text-sm text-slateBlue/70">
            ¬øNo tienes cuenta?{' '}
            <Link to="/register" className="font-medium text-softCoral hover:text-intersectionGreen transition-colors">
              Reg√≠strate
            </Link>
          </p>
        </div>

        {/* Credenciales de demostraci√≥n */}
        <div className="mt-8 p-4 border border-gray-200 rounded-md bg-gray-50">
          <h3 className="text-sm font-medium text-slateBlue mb-2">Credenciales de demostraci√≥n:</h3>
          <p className="text-xs text-slateBlue/70 mb-1">Profesional: demo@aiduxcare.com / password123</p>
          <p className="text-xs text-slateBlue/70 mb-1">Paciente: paciente@aiduxcare.com / password123</p>
          <p className="text-xs text-slateBlue/70">Admin: admin@aiduxcare.com / password123</p>
        </div>
      </div>
    </div>
  );
};

export default LoginPage;

import { useState, FormEvent } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import { userDataSourceSupabase, RoleType } from '../core/services/userDataSourceSupabase';

const RegisterPage = () => {
  const [fullname, setFullname] = useState('');
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [role, setRole] = useState<RoleType>('patient');
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState<string | null>(null);
  const navigate = useNavigate();

  const handleRegister = async (e: FormEvent) => {
    e.preventDefault();

    if (!fullname || !email || !password) {
      setError('Por favor, completa todos los campos');
      return;
    }

    if (password.length < 6) {
      setError('La contrase√±a debe tener al menos 6 caracteres');
      return;
    }

    try {
      setLoading(true);
      setError(null);

      // Usar el servicio de usuarios para registrar
      const data = await userDataSourceSupabase.signUp(email, password, {
        full_name: fullname,
        role
      });

      if (data?.user) {
        // Mostrar mensaje de √©xito y redirigir al login
        alert('Registro exitoso. Por favor inicia sesi√≥n.');
        navigate('/login');
      }
    } catch (error: unknown) {
      const err = error as Error;
      setError(err.message || 'Error al registrar usuario');
      console.error('Error al registrar usuario:', err.message);
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="min-h-screen flex items-center justify-center bg-boneWhite px-4 sm:px-6 lg:px-8 font-sans">
      <div className="w-full max-w-md space-y-8">
